import React, { Component } from "react";
import AlphaVantage from "../utils/apis/alphaVantage";

class Companies extends Component {
  state = {
    companies: {
      quote: null
    }
  };

  componentDidMount() {
    const portifolio = ["BKBR3.SAO"];
    const quote = portifolio.map(async p => {
      let data = null;
      await AlphaVantage.getCurrentQuote(p)
        .then(function(response) {
          data = response.data;
        })
        .catch(function(error) {
          // handle error
        })
        .finally(function() {
          // always executed
        });
      return data;
    });
    let { companies } = this.state;
    companies.quote = quote;
    this.setState({ companies });
  }

  render() {
    return <div>{console.log(this.state)}</div>;
  }
}

export default Companies;
