import React, { Component } from "react";
import AlphaVantage from "../utils/apis/alphaVantage";

class Companies extends Component {
  state = {
    companies: {
      quote: null
    }
  };

  componentDidMount() {
    const portifolio = ["BKBR3.SAO"];
    portifolio.map(p => {
      AlphaVantage.getCurrentQuote(p)
        .then(function(response) {
          let { companies } = this.state;
          companies.quote = response.data;
          this.setState({ companies });
        })
        .catch(function(error) {
          // handle error
        })
        .finally(function() {
          // always executed
        });
    });
  }

  render() {
    return <div>{console.log(this.state)}</div>;
  }
}

export default Companies;
